queries.sparql:
PREFIX cpsv-ap: <http://data.europa.eu/m8g/>
PREFIX cprmv: <https://cprmv.open-regels.nl/0.3.0/>
PREFIX dct: <http://purl.org/dc/terms/>

SELECT ?rule ?title ?extends ?validFrom ?validUntil ?confidence ?description
WHERE {
  ?rule a cpsv-ap:Rule ;
        dct:title ?title ;
        cprmv:extends ?extends ;
        cprmv:validFrom ?validFrom ;
        cprmv:validUntil ?validUntil ;
        cprmv:confidence ?confidence ;
        dct:description ?description .

  FILTER(LANG(?title) = "nl")
  FILTER(LANG(?description) = "nl")
}
ORDER BY ?validFrom

PREFIX cpsv-ap: <http://data.europa.eu/m8g/>
PREFIX dct: <http://purl.org/dc/terms/>

SELECT ?rule ?title ?description
WHERE {
  ?rule a cpsv-ap:Rule ;
        dct:title ?title ;
        dct:description ?description .

  FILTER(LANG(?title) = "nl")
  FILTER(LANG(?description) = "nl")
}
ORDER BY ?title

PREFIX cpsv-ap: <http://data.europa.eu/m8g/>
PREFIX dct: <http://purl.org/dc/terms/>
PREFIX cprmv: <https://cprmv.open-regels.nl/0.3.0/>

SELECT ?service ?title ?description ?goal ?implements ?implementsVersion
WHERE {
  ?service a cpsv-ap:PublicService ;
           dct:title ?title ;
           dct:description ?description ;
           cpsv-ap:goal ?goal ;
           cprmv:implements ?implements ;
           cprmv:implementsVersion ?implementsVersion .

  FILTER(LANG(?title) = "nl")
  FILTER(LANG(?description) = "nl")
  FILTER(LANG(?goal) = "nl")
}

PREFIX dcat: <http://www.w3.org/ns/dcat#>
PREFIX dct: <http://purl.org/dc/terms/>
PREFIX cpsv-ap: <http://data.europa.eu/m8g/>

SELECT ?rule ?distributionTitle ?format ?mediaType ?accessURL ?downloadURL ?byteSize
WHERE {
  ?rule a cpsv-ap:Rule ;
        dcat:distribution ?distribution .
  ?distribution dct:title ?distributionTitle ;
                dct:format ?format ;
                dcat:mediaType ?mediaType ;
                dcat:accessURL ?accessURL ;
                dcat:downloadURL ?downloadURL .
  OPTIONAL { ?distribution dcat:byteSize ?byteSize . }
  FILTER(LANG(?distributionTitle) = "nl" || LANG(?distributionTitle) = "")
}
ORDER BY ?distributionTitle

PREFIX cpsv-ap: <http://data.europa.eu/m8g/>
PREFIX dct: <http://purl.org/dc/terms/>
PREFIX dcat: <http://www.w3.org/ns/dcat#>
PREFIX cprmv: <https://cprmv.open-regels.nl/0.3.0/>

SELECT ?resource ?title ?accessURL ?implements ?implementsVersion
WHERE {
  ?service a cpsv-ap:PublicService ;
           cpsv-ap:hasLegalResource ?resource .
  ?resource dct:title ?title ;
            dcat:accessURL ?accessURL .
  OPTIONAL { ?resource cprmv:implements ?implements . }
  OPTIONAL { ?resource cprmv:implementsVersion ?implementsVersion . }

  FILTER(LANG(?title) = "nl")
}
